changeSet.sql:
CREATE TABLE IF NOT EXISTS art_clinical ( id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, created_date TIMESTAMP WITHOUT TIME ZONE, created_by VARCHAR ( 255 ), last_modified_date TIMESTAMP WITHOUT TIME ZONE, last_modified_by VARCHAR ( 255 ), facility_id BIGINT, visit_id BIGINT, visit_date date, cd_4 VARCHAR ( 255 ), cd_4_percentage BIGINT, is_commencement BOOLEAN, functional_status_id BIGINT, clinical_stage_id BIGINT, clinical_note VARCHAR ( 255 ), uuid VARCHAR ( 255 ) NOT NULL, hiv_enrollment_id BIGINT NOT NULL, regimen_id BIGINT, regimen_type_id BIGINT, art_status_id BIGINT NOT NULL, archived INTEGER, vital_sign_id BIGINT, who_staging_id BIGINT, person_id BIGINT NOT NULL, oi_screened VARCHAR ( 255 ), sti_ids VARCHAR ( 255 ), sti_treated VARCHAR ( 255 ), opportunistic_infections JSONB, adr_screened VARCHAR ( 255 ), adverse_drug_reactions JSONB, adherence_level VARCHAR ( 255 ), adheres JSONB, tb_screen JSONB, next_appointment date, lmp_date date, CONSTRAINT pk_art_clinical PRIMARY KEY ( id ) );
ALTER TABLE art_clinical ADD CONSTRAINT uc_art_clinical_uuid UNIQUE (uuid);
ALTER TABLE art_clinical ADD CONSTRAINT FK_ART_CLINICAL_ON_FACILITY FOREIGN KEY (facility_id) REFERENCES organisation_unit (id);
ALTER TABLE art_clinical ADD CONSTRAINT FK_ART_CLINICAL_ON_HIV_ENROLLMENT FOREIGN KEY (hiv_enrollment_id) REFERENCES hiv_enrollment (id);
ALTER TABLE art_clinical ADD CONSTRAINT FK_ART_CLINICAL_ON_PERSON FOREIGN KEY (person_id) REFERENCES person (id);

CREATE SEQUENCE regimentype_regimentype_id_seq
    START WITH 1
    INCREMENT BY 1
    NO MINVALUE
    NO MAXVALUE CACHE 1;
CREATE TABLE regimen_type
(
    id          bigint DEFAULT nextval('regimentype_regimentype_id_seq'::regclass) NOT NULL,
    description character varying(100)                                             NOT NULL
);

CREATE TABLE regimen_resolver
(
    regimensys character varying(100),
    regimen    character varying(100)
);

CREATE TABLE IF NOT EXISTS art_pharmacy ( id BIGINT GENERATED BY DEFAULT AS IDENTITY NOT NULL, created_date TIMESTAMP WITHOUT TIME ZONE, created_by VARCHAR(255), last_modified_date TIMESTAMP WITHOUT TIME ZONE, last_modified_by VARCHAR(255), facility_id BIGINT, visit_id BIGINT, person_id BIGINT, visit_date date NOT NULL, ard_screened BOOLEAN, prescription_error BOOLEAN, adherence BOOLEAN, mmd_type VARCHAR(255), uuid VARCHAR(255) NOT NULL, next_appointment date NOT NULL, extra JSONB, adverse_drug_reactions JSONB, CONSTRAINT pk_art_pharmacy PRIMARY KEY (id) );
ALTER TABLE art_pharmacy ADD CONSTRAINT uc_art_pharmacy_uuid UNIQUE (uuid);
ALTER TABLE art_pharmacy ADD CONSTRAINT FK_ART_PHARMACY_ON_PERSON FOREIGN KEY (person_id) REFERENCES person (id);
